AJS.toInit(function(){var a=AJS.$("#issueTableDropdown");AJS.$(document).on("click","#open-dialog-button",function(){var b=a.find("option:selected").text();AJS.$("#selected-option").text(b);AJS.dialog2("#issue-table-dialog").show()});AJS.$(document).on("click","#dialog-confirm-button",function(){var b=a.val();console.log("Confirmed selection: "+b);AJS.dialog2("#issue-table-dialog").hide()});AJS.$(document).on("click","#dialog-cancel-button",function(){AJS.dialog2("#issue-table-dialog").hide()})});